import React, { useContext, useState } from 'react';
import axios from 'axios';
import { UserContext } from './UserContext'; // Предположим, что вы используете контекст для передачи данных о пользователе

const ReviewForm = ({ productId }) => {
  const currentUser = useContext(UserContext); // Получаем данные о текущем пользователе из контекста

  const [reviewData, setReviewData] = useState({
    content: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setReviewData({ ...reviewData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:8000/api/products/${productId}/reviews/`, {
        content: reviewData.content,
        user: currentUser.id // Передаем идентификатор текущего пользователя
      });
      console.log(response.data);
      // Дополнительные действия после успешного создания отзыва
    } catch (error) {
      console.error('Error submitting review:', error);
      // Обработка ошибок
    }
  };

  return (
    <div>
      <h2>Leave a Review</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Review:
            <textarea name="content" value={reviewData.content} onChange={handleChange} />
          </label>
        </div>
        <button type="submit">Submit Review</button>
      </form>
    </div>
  );
};

export default ReviewForm;
