import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Header.css';
import SubHeader from './SubHeader';
import { useAuth } from '../../../AuthProvider';
import { useNavigate, Link } from 'react-router-dom';

const Header = () => {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isSearchDropdownOpen, setIsSearchDropdownOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const toggleDropdown = () => {
    setIsDropdownOpen(prevState => !prevState);
  };

  const toggleSearchDropdown = () => {
    setIsSearchDropdownOpen(prevState => !prevState);
  };

  const handleLogout = async () => {
    try {
      await logout();
      setIsDropdownOpen(false);
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  const handleLogin = () => {
    navigate('/login');
  };

  const handleCart = () => {
    navigate('/cart');
  };

  const handleSearchInputChange = (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    if (term.trim() !== '') {
      fetchSearchResults(term);
      toggleSearchDropdown();
    } else {
      setSearchResults([]);
      toggleSearchDropdown();
    }
  };

  const fetchSearchResults = async (term) => {
    try {
      const response = await axios.get(`http://localhost:8000/api/products/?search=${term}`);
      setSearchResults(response.data);
    } catch (error) {
      console.error('Error fetching search results:', error);
    }
  };

  return (
    <header>
      <div className="logo">Lion Composites</div>
      <div className="search-bar">
        <input
          type="text"
          placeholder="–ü–æ–∏—Å–∫"
          value={searchTerm}
          onChange={handleSearchInputChange}
        />
        {isSearchDropdownOpen && (
          <div className="search-dropdown">
            {searchResults.length > 0 ? (
              searchResults.map((product) => (
                <Link
                  key={product.id}
                  to={`/products/${product.id}`}
                  className="search-dropdown-item"
                >
                  {product.name}
                </Link>
              ))
            ) : (
              <div className="search-dropdown-item">–ù–∏—á–µ–≥–æ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ</div>
            )}
          </div>
        )}
      </div>
      <div className="icons">
        <span className="icon">üîç</span>
        <span className="icon">‚öôÔ∏è</span>
      </div>
      <div className="account-btn-container">
        <button className="account-btn" onClick={toggleDropdown}>
          –õ–∏—á–Ω—ã–π –∫–∞–±–∏–Ω–µ—Ç
        </button>
        {isDropdownOpen && (
          <div className="dropdown-menu">
            {user ? (
              <>
                <button className="dropdown-item" onClick={handleCart}>–ö–æ—Ä–∑–∏–Ω–∞</button>
                <button className="dropdown-item" onClick={handleLogout}>
                  –í—ã–π—Ç–∏
                </button>
              </>
            ) : (
              <button className="dropdown-item" onClick={handleLogin}>
                –í–æ–π—Ç–∏
              </button>
            )}
          </div>
        )}
      </div>
    </header>
  );
};

export default Header;
