import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import Header from './components/Header';
import SubHeader from './components/SubHeader';
import Footer from './components/Footer';
import { Link } from 'react-router-dom';
import Slider, { Range } from 'rc-slider';
import 'rc-slider/assets/index.css';

const PageContainer = styled.div`
  background-color: #1A1A1A;
  padding: 20px;
`;

const ProductListContainer = styled.div`
  max-width: 80%;
  margin: 0 auto;
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 20px;
`;

const ProductCard = styled(Link)`
  background-color: #353333;
  border-radius: 20px;
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  box-shadow: 0px 9px 28.3px rgba(0, 0, 0, 0.8);
  text-decoration: none;
  color: inherit;
`;

const ProductImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
  border-radius: 10px;
  margin-bottom: 10px;
`;

const ProductName = styled.h3`
  font-family: Montserrat, sans-serif;
  font-weight: 400;
  font-size: 18px;
  color: #F0F0F0;
  margin-bottom: 5px;
`;

const ProductPrice = styled.p`
  font-family: Montserrat, sans-serif;
  font-weight: 400;
  font-size: 16px;
  color: #F0F0F0;
`;

const FilterContainer = styled.div`
  max-width: 80%;
  margin: 20px auto;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const FilterButton = styled.button`
  font-family: Montserrat, sans-serif;
  font-weight: 400;
  font-size: 16px;
  background-color: #353333;
  color: #F0F0F0;
  border: none;
  border-radius: 5px;
  padding: 10px 20px;
  margin: 0 10px;
  cursor: pointer;
`;

const PriceRangeContainer = styled.div`
  display: flex;
  align-items: center;
  margin-left: 20px;
`;

const PriceRangeLabel = styled.span`
  font-family: Montserrat, sans-serif;
  font-weight: 400;
  font-size: 16px;
  color: #F0F0F0;
  margin-right: 10px;
`;

const ProductListPage = () => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [sortOrder, setSortOrder] = useState(null);
  const [priceRange, setPriceRange] = useState([0, 0]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const productsResponse = await axios.get('http://localhost:8000/api/products/');
        setProducts(productsResponse.data);

        const categoriesResponse = await axios.get('http://localhost:8000/api/categories/');
        setCategories(categoriesResponse.data);

        const minPrice = Math.min(...productsResponse.data.map((product) => product.cost));
        const maxPrice = Math.max(...productsResponse.data.map((product) => product.cost));
        setPriceRange([minPrice, maxPrice]);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleCategoryFilter = (categoryId) => {
    setSelectedCategory(categoryId);
  };

  const handleSortOrder = (order) => {
    setSortOrder(order);
  };

  const handlePriceRangeChange = (values) => {
    setPriceRange(values);
  };

  let filteredProducts = products.filter((product) =>
    selectedCategory ? product.category === selectedCategory : true
  );

  filteredProducts = filteredProducts.filter((product) =>
    product.cost >= priceRange[0] && product.cost <= priceRange[1]
  );

  if (sortOrder === 'low-to-high') {
    filteredProducts.sort((a, b) => a.cost - b.cost);
  } else if (sortOrder === 'high-to-low') {
    filteredProducts.sort((a, b) => b.cost - a.cost);
  }

  return (
    <PageContainer>
      <Header />
      <SubHeader />
      <FilterContainer>
        <FilterButton onClick={() => handleCategoryFilter(null)}>Все категории</FilterButton>
        {categories.map((category) => (
          <FilterButton key={category.id} onClick={() => handleCategoryFilter(category.id)}>
            {category.name}
          </FilterButton>
        ))}
        <FilterButton onClick={() => handleSortOrder('low-to-high')}>Сначала дешевые</FilterButton>
        <FilterButton onClick={() => handleSortOrder('high-to-low')}>Сначала дорогие</FilterButton>
        <PriceRangeContainer>
          <PriceRangeLabel>Цена:</PriceRangeLabel>
          <Range
            min={priceRange[0]}
            max={priceRange[1]}
            defaultValue={priceRange}
            onChange={handlePriceRangeChange}
            trackStyle={[{ backgroundColor: '#F0F0F0' }]}
            handleStyle={[{ backgroundColor: '#F0F0F0', borderColor: '#F0F0F0' }]}
          />
        </PriceRangeContainer>
      </FilterContainer>
      <ProductListContainer>
        {filteredProducts.map((product) => (
          <ProductCard key={product.id} to={`/product/${product.id}`}>
            <ProductImage src={product.image} alt={product.name} />
            <ProductName>{product.name}</ProductName>
            <ProductPrice>Цена: {product.cost} руб.</ProductPrice>
          </ProductCard>
        ))}
      </ProductListContainer>
      <Footer />
    </PageContainer>
  );
};

export default ProductListPage;
