import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const ReviewContainer = styled.div`
    margin-top: 20px;
`;

const ReviewContent = styled.p`
    color: white; /* Установка цвета текста на белый */
`;

const Review = ({ productId }) => {
    const [reviews, setReviews] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:8000/api/products/${productId}/reviews`)
            .then(response => {
                setReviews(response.data);
            })
            .catch(error => {
                console.error('Error fetching reviews:', error);
            });
    }, [productId]);

    return (
        <ReviewContainer>
            {reviews.map(review => (
                <div key={review.id}>
                    <ReviewContent>User: {review.user}</ReviewContent>
                    <ReviewContent>{review.content}</ReviewContent> {/* Применение стилей к тексту комментария */}
                </div>
            ))}
        </ReviewContainer>
    );
}

export default Review;
