// ProductDetail.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import Header from './components/Header';
import SubHeader from './components/SubHeader';
import Review from './components/Review';

import 'typeface-montserrat';

const PageContainer = styled.div`
    background-color: #1A1A1A;
`;

const Container = styled.div`
    max-width: 70%;
    margin: 0px auto 20px;
    padding: 20px;
    display: flex;
    background-color: #1A1A1A;
`;

const ProductNameContainer = styled.div`
    max-width: 70%;
    margin: 0px auto;
`;

const ProductImageContainer = styled.div`
    flex: 1;
    margin-right: 100px;
    width: 100%;
`;

const ProductInfoContainer = styled.div`
    flex: 1;
    display: flex;
    flex-direction: column;
`;

const ProductInfo = styled.div`
    flex: 1;
    margin-right: 60px;
`;

const ProductImage = styled.img`
    width: 100%;
    height: auto;
    max-width: 700px;
    height: 600px;
    width: 500px;
    margin-right: 40px;
    margin-bottom: 40px;
    box-shadow: 0px 9px 28.3px rgba(0, 0, 0, 0.8);
    border-radius: 47px;
`;

const ProductName = styled.h1`
    font-family: Montserrat, sans-serif;
    font-weight: 400;
    font-size: 64px;
    margin-bottom: 20px;
    color: #F0F0F0;
`;

const Characteristics = styled.ul`
    font-family: Montserrat, sans-serif;
    font-weight: 300;
    font-size: 30px;
    list-style-type: disc;
    padding-left: 20px;
    color: #F0F0F0;
`;

const ProductDescription = styled.div`
    margin-top: 40px;
    width: 70%;
    height:auto;
    margin: 0px auto 20px;
    padding: 20px;
    background-color: #353333;
    box-shadow: 0px 9px 28.3px rgba(0, 0, 0, 0.8);
    border-radius: 47px;
    position: relative;
`;

const Description = styled.p`
    font-family: Montserrat, sans-serif;
    font-weight: 400;
    font-size: 24px;
    margin-bottom: 5%;
    color: #F0F0F0;
`;

const Desc = styled.h3`
    font-family: Montserrat, sans-serif;
    font-weight: 400;
    font-size: 48px;
    color: #F0F0F0;
    margin-top: 1%;
    margin-bottom: 20px;
    text-align: center;
`;

const Price = styled.p`
    font-family: Montserrat, sans-serif;
    font-weight: 400;
    font-size: 36px;
    color: #F0F0F0;
    border-bottom: 1px solid #FFFFFF;
`;

const Button = styled.button`
    font-family: Montserrat, sans-serif;
    font-weight: 600;
    font-size: 24px;
    padding: 10px 20px;
    background-color: #F0F0F0;
    color: #1A1A1A;
    font-family: Montserrat, sans-serif;
    font-weight: 400;
    font-size: 30px;
    border: none;
    cursor: pointer;
    position: absolute;
    bottom: 20px;
    right: 20px;
    margin-top: 60px;
    box-shadow: 0px 9px 28.3px rgba(0, 0, 0, 0.8);
    border-radius: 16px;
`;

const ButtonContainer = styled.div`
    width: 100%;
    background-color: black;
    margin-top: 8%;
`;

const FixedHeader = styled(Header)`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    z-index: 999;
`;

const Reviews = styled.h3`
    font-family: Montserrat, sans-serif;
    font-weight: 400;
    font-size: 60px;
    color: #F0F0F0;
    margin-top: 7%;
    margin-bottom: 20px;
    text-align: center;
`;

const AuthMessage = styled.p`
    font-family: Montserrat, sans-serif;
    font-weight: 400;
    font-size: 24px;
    color: #F0F0F0;
    margin-top: 20px;
    text-align: center;
`;

const ProductDetail = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        axios.get(`http://localhost:8000/api/products/${id}`)
            .then(response => {
                setProduct(response.data);
            })
            .catch(error => {
                console.error('Error fetching product:', error);
            });

        axios.get(`http://localhost:8000/api/check-auth/`)
            .then(response => {
                setIsAuthenticated(response.data.isAuthenticated);
            })
            .catch(error => {
                console.error('Error checking authentication status:', error);
            });
    }, [id]);

    if (!product) {
        return <div>Loading...</div>;
    }

    const characteristicsList = product.properties.split('|');

    const handleLogout = () => {
        axios.post(`http://localhost:8000/api/logout/`)
            .then(() => {
                setIsAuthenticated(false);
            })
            .catch(error => {
                console.error('Error logging out:', error);
            });
    };

    return (
        <PageContainer>
            <FixedHeader />
            <SubHeader />
            <Container>
                <ProductNameContainer>
                    <ProductName>{product.name}</ProductName>
                    <ButtonContainer>
                        {isAuthenticated && <Button onClick={handleLogout}>Logout</Button>}
                    </ButtonContainer>
                </ProductNameContainer>
                <ProductImageContainer>
                    <ProductImage src={product.image} alt={product.name} />
                </ProductImageContainer>
                <ProductInfoContainer>
                    <ProductInfo>
                        <Price>{product.cost}</Price>
                        <Characteristics>
                            {characteristicsList.map((char, index) => (
                                <li key={index}>{char}</li>
                            ))}
                        </Characteristics>
                        <ProductDescription>
                            <Desc>Description</Desc>
                            <Description>{product.description}</Description>
                        </ProductDescription>
                    </ProductInfo>
                </ProductInfoContainer>
            </Container>
            <Reviews>Reviews</Reviews>
            {isAuthenticated ? (
                <AuthMessage>User is authenticated</AuthMessage>
            ) : (
                <AuthMessage>User is not authenticated</AuthMessage>
            )}
            <Review productId={id} />
        </PageContainer>
    );
};

export default ProductDetail;
