import React, { useEffect, useState } from 'react';
import ProductCardList from './components/ProductCard';
import axios from 'axios';
import styled from 'styled-components';


const CategoryContainer = styled.div`
    display: flex;
    align-items: center;
    width: 50px;
    height: 750px;
    border-radius: 31px;
    background-color: #FFCD6E;
    margin-bottom: 20px;
`;

const CategoryTitle = styled.h2`
    margin-right: 20px;
`;

const ProductLinksContainer = styled.div`
    flex-grow: 1; /* Делаем контейнер ссылок на товары расширяемым, чтобы он занимал все доступное пространство справа */
`;

function ProductList() {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:8000/api/categories/`)
            .then(response => {
                setCategories(response.data);
            })
            .catch(error => {
                console.error('Error fetching categories:', error);
            });
    }, []);

    return (
        <div>
            {categories.map(category => (
                <CategoryContainer key={category.id}>
                    <CategoryTitle>{category.name}</CategoryTitle>
                    <ProductLinksContainer>
                        <ProductCardList categoryId={category.id} />
                    </ProductLinksContainer>
                </CategoryContainer>
            ))}
        </div>
    );
}

export default ProductList;
