import React, { useState } from 'react';
import axios from 'axios'; // Для выполнения HTTP-запросов

const RegistrationForm = () => {
  // Остальной код остается без изменений

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Отправка данных на сервер
      const response = await axios.post('/api/register/', {
        name: firstName,
        second_name: lastName,
        login: username,
        password: password,
        email: email,
        phone_number: phoneNumber,
      });
      console.log(response.data);
      // Дополнительные действия после успешной регистрации, например, перенаправление пользователя на другую страницу
    } catch (error) {
      console.error(error);
      // Обработка ошибок, например, вывод сообщения об ошибке пользователю
    }
  };


  return (
    <div>
      <h2>Регистрация</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Имя:
            <input type="text" value={firstName} onChange={handleFirstNameChange} />
          </label>
        </div>
        <div>
          <label>
            Фамилия:
            <input type="text" value={lastName} onChange={handleLastNameChange} />
          </label>
        </div>
        <div>
          <label>
            Логин:
            <input type="text" value={username} onChange={handleUsernameChange} />
          </label>
        </div>
        <div>
          <label>
            Пароль:
            <input type="password" value={password} onChange={handlePasswordChange} />
          </label>
        </div>
        <div>
          <label>
            Почта:
            <input type="email" value={email} onChange={handleEmailChange} />
          </label>
        </div>
        <div>
          <label>
            Номер телефона:
            <input type="tel" value={phoneNumber} onChange={handlePhoneNumberChange} />
          </label>
        </div>
        <button type="submit">Зарегистрироваться</button>
      </form>
    </div>
  );
};

export default RegistrationForm;
