import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import Header from './components/Header';
import SubHeader from './components/SubHeader';
import styled from 'styled-components';

// Создаем стилизованные компоненты для изображения товара и контейнера для характеристик
const ProductImage = styled.img`
    max-width: 100%;
    height: auto;
    margin-bottom: 20px;
`;

const ProductDetailsContainer = styled.div`
    display: flex;
    flex-direction: column;
`;

const ProductDetail = () => {
    const { id } = useParams(); // Получаем id из URL
    const [product, setProduct] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:8000/api/products/${id}`)
            .then(response => {
                setProduct(response.data);
            })
            .catch(error => {
                console.error('Error fetching product:', error);
            });
    }, [id]); // Добавляем id в зависимости, чтобы запрос отправлялся при изменении id

    if (!product) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <Header />
            <SubHeader />
            <h1>{product.name}</h1>
            <ProductImage src={product.image} alt={product.name} />
            <ProductDetailsContainer>
                <p><strong>Характеристики:</strong> {product.properties}</p>
                <p><strong>Описание:</strong> {product.description}</p>
                <p><strong>Цена:</strong> {product.cost}</p>
            </ProductDetailsContainer>
        </div>
    );
}

export default ProductDetail;
