import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
    console.log('AuthProvider rendering');
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Функция для чтения токена из cookie
  const getCookie = (name) => {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';').shift();
  };

  // Проверка аутентификации при загрузке страницы
  useEffect(() => {
    const token = getCookie('auth_token');
    if (token) {
      // Здесь вы можете выполнить запрос к серверу для проверки валидности токена
      // Например, вы можете отправить токен на сервер и проверить его срок действия
      // Если токен действителен, установите пользователя в состояние аутентификации
      // Если нет, вы можете очистить токен и пользователя из состояния
      setUser({ isAuthenticated: true });
    }
    setLoading(false);
  }, []);

  // Обновляем состояние пользователя при изменении
  useEffect(() => {
    console.log('User state updated:', user);
  }, [user]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <AuthContext.Provider value={{ user, setUser }}>
      {children}
    </AuthContext.Provider>
  );
};
