import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const ReviewContainer = styled.div`
    margin-top: 20px;
`;

const ReviewBox = styled.div`
    width: 753px;
    height: 468px;
    background-color: #353333;
    border-radius: 40px;
    box-shadow: 0px 15px 29.4px rgba(0, 0, 0, 0.5);
    padding: 20px;
    margin-bottom: 20px;
`;

const UserName = styled.h4`
    color: white;
    margin-bottom: 10px;
`;

const ReviewContent = styled.p`
    color: white;
`;

const Review = ({ productId }) => {
    const [reviews, setReviews] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:8000/api/products/${productId}/reviews`)
            .then(response => {
                setReviews(response.data);
            })
            .catch(error => {
                console.error('Error fetching reviews:', error);
            });
    }, [productId]);

    // Функция для получения имени пользователя по его id
    const getUserName = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:8000/api/users/${userId}`);
            return response.data.username; // Возвращаем имя пользователя из полученных данных
        } catch (error) {
            console.error('Error fetching user:', error);
            return ''; // Возвращаем пустую строку в случае ошибки
        }
    };

    return (
        <ReviewContainer>
            {reviews.map(async (review) => (
                <ReviewBox key={review.id}>
                    <UserName>{await getUserName(review.user)}</UserName>
                    <ReviewContent>{review.content}</ReviewContent>
                </ReviewBox>
            ))}
        </ReviewContainer>
    );
}

export default Review;
