// App.js
import React from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import ProductList from './ProductList';
import ProductDetail from './ProductDetail'; // Предполагается, что этот компонент еще не создан

const App = () => {
    return (
        <Router>
            <div>
                <nav>
                    <ul>
                        <li>
                            <Link to="/">Home</Link>
                        </li>
                        <li>
                            <Link to="/products">Products</Link>
                        </li>
                    </ul>
                </nav>

                <Route path="/" exact component={Home} />
                <Route path="/products" component={ProductList} />
                <Route path="/product/:id" component={ProductDetail} /> {/* Параметр :id будет передаваться в ProductDetail */}
            </div>
        </Router>
    );
}

const Home = () => {
    return (
        <div>
            <h1>Welcome to the Home Page</h1>
            <p>This is the main page of our React application.</p>
        </div>
    );
}

export default App;
