import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const ReviewContainer = styled.div`
    margin-top: 20px;
`;

const ReviewBox = styled.div`
    width: 753px;
    height: 468px;
    background-color: #353333;
    border-radius: 40px;
    box-shadow: 0px 15px 29.4px rgba(0, 0, 0, 0.5);
    padding: 20px;
    margin-bottom: 20px;
`;

const UserName = styled.h4`
    color: white;
    margin-bottom: 10px;
`;

const ReviewContent = styled.p`
    color: white;
`;

const Review = ({ productId }) => {
    const [reviews, setReviews] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:8000/api/products/${productId}/reviews`)
            .then(response => {
                setReviews(response.data);
            })
            .catch(error => {
                console.error('Error fetching reviews:', error);
            });
    }, [productId]);

    return (
        <ReviewContainer>
            {reviews.map(review => (
                <div key={review.id}>
                    <ReviewContent>User: {review.user}</ReviewContent>
                    <ReviewContent>{review.content}</ReviewContent> {/* Применение стилей к тексту комментария */}
                </div>
            ))}
        </ReviewContainer>
    );
}

export default Review;
