import React, { useEffect, useState } from 'react';
import ProductCardList from './components/ProductCard';
import axios from 'axios';
import styled from 'styled-components';


const CategoryContainer = styled.div`
    display: flex; /* Делаем контейнер flex */
    align-items: center; /* Выравниваем элементы по вертикали */
    width: 100%; /* Чтобы контейнер занимал всю ширину */
    margin-bottom: 20px; /* Добавляем немного отступа между категориями */
`;

const CategoryTitle = styled.h2`
    margin-right: 20px; /* Добавляем отступ между заголовком категории и товарами */
`;

function ProductList() {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:8000/api/categories/`)
            .then(response => {
                setCategories(response.data);
            })
            .catch(error => {
                console.error('Error fetching categories:', error);
            });
    }, []);

    return (
        <div>
            {categories.map(category => (
                <CategoryContainer key={category.id}>
                    <CategoryTitle>{category.name}</CategoryTitle>
                    <ProductCardList categoryId={category.id} />
                </CategoryContainer>
            ))}
        </div>
    );
}

export default ProductList;
