import React, { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { useAuth } from '../../../AuthProvider'; // Импортируем хук useAuth

const FormContainer = styled.div`
  margin-top: 20px;
`;

const TextArea = styled.textarea`
  width: 100%;
  height: 150px;
`;

const SubmitButton = styled.button`
  margin-top: 10px;
`;

const ReviewForm = ({ productId }) => {
  const [reviewData, setReviewData] = useState({
    content: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setReviewData({ ...reviewData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const user = JSON.parse(localStorage.getItem('user'));
      await axios.post(
        `http://localhost:8000/api/products/${productId}/reviews/`,
        {
          user: user.id,
          content: reviewData.content
        },
        {
          headers: {
            Authorization: `Token ${token}`
          }
        }
      );
      console.log('Отзыв успешно отправлен');
      setReviewData({ content: '' }); // Очищаем поле ввода после успешной отправки отзыва
    } catch (error) {
      console.error('Ошибка при отправке отзыва:', error);
    }
  };

  return (
    <div>
      <h2>Добавить отзыв</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Содержание отзыва:
            <textarea name="content" value={reviewData.content} onChange={handleChange} />
          </label>
        </div>
        <button type="submit">Отправить отзыв</button>
      </form>
    </div>
  );
};

export default ReviewForm;
