import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
    console.log('AuthProvider rendering');
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Функция для чтения токена из cookie
  const getCookie = (name) => {
    const value = `; ${document.cookie}`;
    const parts = value.split(`; ${name}=`);
    if (parts.length === 2) return parts.pop().split(';').shift();
  };

  // Проверка аутентификации при загрузке страницы
  useEffect(() => {
  const token = getCookie('auth_token');
  if (token) {
    axios.get(`http://localhost:8000/api/current_user/`, {
      headers: {
        Authorization: `Token ${token}`
      }
    })
    .then(response => {
      const user = response.data;
      setUser({ isAuthenticated: true, id: user.id }); // Добавляем сохранение id пользователя
      document.cookie = `user_id=${user.id}`; // Сохраняем id пользователя в cookie
    })
    .catch(error => {
      console.error('Error fetching current user:', error);
      setUser(null);
    });
  } else {
    setUser(null);
  }
}, []);


  useEffect(() => {
  console.log('User ID:', user ? user.id : 'Not authenticated');
}, [user]);

  // Обновляем состояние пользователя при изменении
  useEffect(() => {
    console.log('User state updated:', user);
  }, [user]);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <AuthContext.Provider value={{ user, setUser }}>
      {children}
    </AuthContext.Provider>
  );
};
