// AboutUsPage.js

import React from 'react';
import Header from './components/Header';
import SubHeader from './components/SubHeader';

const AboutUsPage = () => {
 const rectangleStyles = {
        width: '1333px', // Ширина
        height: '247px', // Высота
        backgroundColor: '#353333', // Цвет фона
        borderRadius: '40px', // Скругление углов
        boxShadow: '0px 11px 28.7px rgba(0, 0, 0, 0.25)', // Тень
        margin: '0 auto', // Автоматическое выравнивание по горизонтали
        marginTop: '20px', // Отступ сверху
    };
    // Объект стилей для страницы "О нас"
    const pageStyles = {
        backgroundColor: '#1A1A1A', // Цвет фона страницы
        textAlign: 'center', // Выравнивание текста по центру
    };

    // Объект стилей для заголовка "О нас"
    const headingStyles = {
        fontFamily: 'Montserrat, sans-serif', // Шрифт заголовка
        fontWeight: 400, // Стиль Regular
        fontSize: '64px', // Размер шрифта
        color: '#fff', // Цвет текста
        marginTop: '100px', // Верхний отступ для центрирования
    };

    return (
        <div style={pageStyles}> {/* Применяем стили к контейнеру страницы */}
            <Header />
            <SubHeader />
            <div className="about-content">
                <h1 style={headingStyles}>Где мы находимся?</h1> {/* Применяем стили к заголовку */}
                <p>Здесь вы можете написать краткую информацию о вашем магазине, его истории, целях и ценностях.</p>
                {/* Добавьте здесь любую дополнительную информацию */}
            </div>
        </div>
    );
};

export default AboutUsPage;
