import React from 'react';
import styled from 'styled-components'; // Импортируем styled-components
import Header from './components/Header'; // Подключаем компонент Header
import SubHeader from './components/SubHeader'; // Подключаем компонент SubHeader

// Остальной ваш код...

const PageContainer = styled.div`
    background-color: #1A1A1A; /* Устанавливаем цвет фона для всей страницы */
    color: #F0F0F0; /* Устанавливаем цвет текста */
    font-family: Montserrat, sans-serif; /* Устанавливаем шрифт */
`;

const DeliveryPageTitle = styled.h1`
    font-family: Montserrat, sans-serif; /* Устанавливаем шрифт Montserrat */
    font-weight: 400; /* Устанавливаем стиль Regular */
    font-size: 64px; /* Устанавливаем размер 64px */
    margin-bottom: 20px; /* Отступ снизу заголовка */
    text-align: center; /* Выравнивание текста по центру */
    color: #FFFFFF; /* Цвет заголовка */
`;

const DeliveryPageContent = styled.div`
    max-width: 70%;
    margin: 0 auto;
    padding: 20px;
`;

const ContentContainer = styled.div`
    position: relative;
`;

const BackgroundImage = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url('./Rectangle43.png');
    background-size: cover; /* Растягивает изображение, чтобы оно покрыло весь блок */
    background-position: center; /* Выравнивает изображение по центру блока */
    border-radius: 31px; /* Задает скругление углов */
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25); /* Тень */
    z-index: -1; /* Помещаем изображение под контент */
`;

const Content = styled.div`
    z-index: 1; /* Помещаем контент поверх изображения */
    /* Ваши стили для контента */
`;

const DeliveryPage = () => {
    return (
        <PageContainer>
            <Header />
            <SubHeader />
            <ContentContainer>
                <BackgroundImage />
                <Content>
                    <h1>Как мы работаем с клиентами</h1>
                    {/* Ваш контент здесь */}
                </Content>
            </ContentContainer>
        </PageContainer>
    );
};

export default DeliveryPage;
