    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:8000/api/products/${productId}/reviews/`, {
        content: reviewData.content,
        user: currentUser.id // Передаем идентификатор текущего пользователя
      });
      console.log(response.data);
      // Дополнительные действия после успешного создания отзыва
    } catch (error) {
      console.error('Error submitting review:', error);
      // Обработка ошибок
    }
  };

  return (
    <div>
      <h3>Оставить отзыв</h3>
      <form onSubmit={handleSubmit}>
        <div>
          <textarea
            name="content"
            value={reviewData.content}
            onChange={handleChange}
            placeholder="Напишите ваш отзыв здесь..."
            rows="4"
            cols="50"
            required
          />
        </div>
        <button type="submit">Отправить отзыв</button>
      </form>
    </div>
  );
};

export default ReviewForm;
