import React, { useEffect } from "react";
import './Map.css';

const MapComponent = () => {
    useEffect(() => {
        const script = document.createElement("script");

        // Функция, которая будет выполняться после загрузки API Яндекс.Карт
        script.onload = () => {
            // Создание экземпляра карты
            const map = new window.ymaps.Map("map", {
                center: [39.686249, 47.279403], // Координаты центра карты
                zoom: 12 // Уровень масштабирования карты
            });

            // Создание геометки (метки) на карте
            const placemark = new window.ymaps.Placemark([39.686249, 47.279403], {
                hintContent: 'Место на карте',
                balloonContent: 'Это место, которое вы выбрали'
            });

            // Добавление геометки на карту
            map.geoObjects.add(placemark);
        };

        // Загрузка API Яндекс.Карт
        script.src = "https://yandex.ru/map-widget/v1/?um=constructor%3A708f2da14b7c06ea6e59eddb02a9c4e54b9627268b3b8e5fbd0b1ff3c96326b0&amp;source=constructor&amp;";
        script.async = true;
        document.body.appendChild(script);

        // Очистка после себя
        return () => {
            document.body.removeChild(script);
        };
    }, []);

    return (
        <div id="map" style={{ width: "604px", height: "450px", borderRadius: "45px", overflow: "hidden" }} />
    );
};

export default MapComponent;
