import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import ProductList from './pages/product_page/product_page';
import ProductDetail from './pages/product_page/ProductDetail'; // Новый компонент
import RegistrationForm from './pages/registration_form/RegistrationForm';
import LoginForm from './pages/registration_form/LoginForm';
import { AuthProvider } from './AuthProvider';
import { useAuth } from './AuthProvider';
import axios from 'axios';

const App = () => {
  const { setUser } = useAuth();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get('http://localhost:8000/api/check-auth/', {
        headers: {
          Authorization: `Token ${token}`
        }
      })
      .then(response => {
        setUser(response.data.user);
      })
      .catch(error => {
        console.error('Error checking authentication:', error);
        // Если произошла ошибка при проверке аутентификации, перенаправляем на страницу входа
        // Мы не можем использовать Navigate здесь, так что просто обновим страницу, чтобы перейти на маршрут входа
        window.location.reload();
      });
    }
  }, [setUser]);
    return (
    <AuthProvider>
        <Router>
            <Routes>
                <Route path="/" element={<ProductList />} />
                <Route path="/product/:id" element={<ProductDetail />} /> {/* Новый маршрут */}
                <Route path="/registration" element={<RegistrationForm />} />
                <Route path="/login" element={<LoginForm />} />
            </Routes>
        </Router>
    </AuthProvider>
    );
}

const Home = () => {
    return (
        <div>
            <h1>Welcome to the Home Page</h1>
            <p>This is the main page of our React application.</p>
        </div>
    );
}

export default App;
