import React from 'react';
import Header from './components/Header';
import SubHeader from './components/SubHeader';
import styled from 'styled-components';
import Footer from './components/Footer';
import MapComponent from './components/Map';

const PageContainer = styled.div`
    background-color: #1A1A1A;
`;

const ContentContainer = styled.div`
    max-width: 70%;
    margin: 0 auto;
    padding: 20px;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: flex-start; /* Изменено на flex-start */
    background-color: #1A1A1A;
`;

const Rectangle = styled.div`
    position: relative;
    width: 100%;
    background-color: #353333;
    border-radius: 40px;
    box-shadow: 0px 11px 28.7px rgba(0, 0, 0, 0.25);
    margin-top: 7%;
    padding: 30px;
`;

const SmallRectangleLeft = styled.div`
    width: 331px;
    height: 392px;
    position: relative;
    background-color: #353333;
    border-radius: 40px;
    box-shadow: 0px 10px 20.3px rgba(0, 0, 0, 0.25);
    margin-top: 15px;
    left: -1%;
    padding: 30px;
    margin-right: 0.5%;
`;




const Heading = styled.h1`
    font-family: Montserrat, sans-serif;
    font-weight: 400;
    font-size: 64px;
    color: #F0F0F0;
    margin-bottom: 20px;
`;

const ContactList = styled.ul`
    font-family: Montserrat, sans-serif;
    font-weight: 300;
    font-size: 32px;
    color: #F0F0F0;
    list-style-type: disc;
`;

const ContactListItem = styled.li`
    margin-bottom: 10px;
`;

const AddressTitle = styled.h2`
    font-family: Montserrat, sans-serif;
    font-weight: 500;
    font-size: 48px;
    color: #F0F0F0;
    margin-bottom: 20px;
`;

const AddressText = styled.p`
    font-family: Montserrat, sans-serif;
    font-weight: 300;
    font-size: 32px;
    color: #F0F0F0;
`;
const InfoRectangle = styled.div`
    width: 331px;
    height: 392px;
    background-color: #353333;
    border-radius: 40px;
    box-shadow: 0px 10px 20.3px rgba(0, 0, 0, 0.25);
    margin-top: 15px;
    margin-left: 20px; /* Изменено на 20px */
    padding: 30px;
    color: #F0F0F0;
`;

const InfoTitle = styled.h2`
    font-family: Montserrat, sans-serif;
    font-weight: 500; /* Стиль Medium */
    font-size: 48px;
`;

const InfoText = styled.p`
    font-family: Montserrat, sans-serif;
    font-weight: 300; /* Стиль Lighter */
    font-size: 32px;
`;

const AboutRectangle = styled.div`
    width: 100%; /* Ширина 70% страницы */
    margin: 0 auto; /* Центрирование по горизонтали */
    margin: 1% auto 0;
    background-color: #353333;
    border-radius: 40px;
    box-shadow: 0px 11px 28.7px rgba(0, 0, 0, 0.25); /* Тень со сдвигом по y на 11px и blur 28.7 */
    padding: 30px;
    color: #F0F0F0;
    position: relative;
`;

const AboutTitle = styled.h2`
    font-family: Montserrat, sans-serif;
    font-weight: 500; /* Стиль Medium */
    font-size: 48px;
    margin-bottom: 20px; /* Отступ снизу */
`;

const AboutText = styled.p`
    font-family: Montserrat, sans-serif;
    font-weight: 300; /* Стиль Lighter */
    font-size: 32px;
`;

const AdditionalTitle = styled.h2`
    font-family: Montserrat, sans-serif;
    font-weight: 500;
    font-size: 48px;
    margin-top: 25%;
    position: relative; /* Изменено на absolute */
    left: -5%; /* Выравнивание по левому краю */
    top: 0; /* Выравнивание по верхнему краю */
    color: #F0F0F0;
`;

const AdditionalRectangle = styled.div`
    width: 550px;
    height: 600px;
    background-color: #353333;
    border-radius: 40px;
    box-shadow: 0px 11px 28.7px rgba(0, 0, 0, 0.25);
    position: relative;
    left: 9%;
    margin-top: 5%; /* Размещаем прямоугольник под надписью с дополнительным отступом 30px */
    padding: 30px;
    color: #F0F0F0;
`;

const AdditionalText = styled.p`
    font-family: Montserrat, sans-serif;
    font-weight: 300;
    font-size: 45px;
    color: #F0F0F0;
`;

const CooperationTitle = styled.h3`
    font-family: Montserrat, sans-serif;
    font-weight: 500;
    font-size: 48px;
    color: #FFFFFF;
    position: relative;
    margin-top: 25%; /* Дополнительный отступ сверху */
    margin-right: 10%; /* Дополнительный отступ справа */
`;


const AboutUsPage = () => {
    return (
        <PageContainer>
            <Header />
            <SubHeader />
            <ContentContainer>
            <Heading>Где мы находимся?</Heading>
                <Rectangle>
                    <AddressTitle>Адрес</AddressTitle>
                    <AddressText>ул. Вавилова 68/2, г. Ростов-на-Дону (магазин находится на территории Базы "РОСАВТОМАТИК"), Ростовская область, Россия</AddressText>
                </Rectangle>
                <SmallRectangleLeft>
                    <AddressTitle>Контакты:</AddressTitle>
