import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import Header from './components/Header';
import SubHeader from './components/SubHeader';
import Review from './components/Review';
import { useAuth } from '../../AuthProvider';

import 'typeface-montserrat';

const ProductDetail = () => {
    const { id } = useParams();
    const { user } = useAuth();
    const [product, setProduct] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await axios.get(`http://localhost:8000/api/products/${id}/`);
                setProduct(response.data);
            } catch (error) {
                console.error('Error fetching product:', error);
            }
        };

        fetchProduct();
    }, [id]);

    if (!user) {
        return <div>Loading...</div>;
    }

    // Разделение характеристик товара
    const characteristicsList = product.properties.split('|');

    return (
        <PageContainer>
            <Header />
            <SubHeader />
            <ProductNameContainer>
                <ProductName>{product.name}</ProductName>
            </ProductNameContainer>
            <Container>
                <ProductImageContainer>
                    <ProductImage src={product.image} alt={product.name} />
                </ProductImageContainer>
                <ProductInfoContainer>
                    <Characteristics>
                        {characteristicsList.map((characteristic, index) => (
                            <li key={index}>{characteristic}</li>
                        ))}
                    </Characteristics>
                </ProductInfoContainer>

            </Container>
            <ProductDescription>
                <Desc> Описание </Desc>
                <Description>{product.description}</Description>
                <Price>Цена: {product.cost} руб.</Price>
                <ButtonContainer>
                    <Button>В корзину</Button>
                </ButtonContainer>
            </ProductDescription>
            <Reviews> Отзывы </Reviews>
            {/* Conditional rendering of "Оставить отзыв" button */}
            {user.isAuthenticated && <AddReviewButton>Оставить отзыв</AddReviewButton>}
            <Review productId={id} />
        </PageContainer>
    );
}

export default ProductDetail;
