import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const ProductLink = styled(Link)`
    width: 304px;
    height: 350px;
    background-color: #DDDDDD; /* Серый фон */
    border-radius: 15px;
    display: flex;
    justify-content: center;
    align-items: center;
    margin-bottom: 20px;
    text-decoration: none; /* Убираем подчеркивание */
`;

function ProductCardList({ categoryId }) {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        if (!categoryId) return; // Не делаем запрос, если categoryId пуст

        axios.get(`http://localhost:8000/api/products/?category=${categoryId}`)
            .then(response => {
                setProducts(response.data);
            })
            .catch(error => {
                console.error(`Error fetching products for category ${categoryId}:`, error);
            });
    }, [categoryId]);

    // Фильтрация товаров по категории
    const filteredProducts = products.filter(product => product.category === categoryId);

    return (
        <div>
            {filteredProducts.map(product => (
                <ProductLink key={product.id} to={`/product/${product.id}`}>
                    {product.name}
                </ProductLink>
            ))}
        </div>
    );
}

function ProductCard({ product }) {
    return (
        <div>
            <h3><Link to={`/product/${product.id}`}>{product.name}</Link></h3>
        </div>
    );
}

export default ProductCardList;
