import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginForm = () => {
  const [userData, setUserData] = useState({
    username: '',
    password: ''
  });

  const [error, setError] = useState(null); // Состояние для отображения ошибок

  const navigate = useNavigate(); // Использование useNavigate для программной навигации

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:8000/api/login/`, userData);
      const token = response.data.token; // Получаем токен из ответа сервера
      localStorage.setItem('token', token); // Сохраняем токен в localStorage или другом месте
      navigate('/'); // Перенаправляем пользователя на страницу после успешной авторизации
    } catch (error) {
      console.error('Error logging in:', error);
      setError('Неправильное имя пользователя или пароль'); // Установка состояния ошибки
    }
  };

  return (
    <div>
      <h2>Авторизация</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Отображение ошибки, если есть */}
      <form onSubmit={handleLogin}>
        <div>
          <label>
            Логин:
            <input type="text" name="username" value={userData.username} onChange={handleChange} />
          </label>
        </div>
        <div>
          <label>
            Пароль:
            <input type="password" name="password" value={userData.password} onChange={handleChange} />
          </label>
        </div>
        <button type="submit">Войти</button>
      </form>
    </div>
  );
};

export default LoginForm;
