// LoginForm.js

import React, { useState } from 'react';
import axios from 'axios';
import { Redirect } from 'react-router-dom'; // Импорт Redirect

const LoginForm = () => {
  const [userData, setUserData] = useState({
    username: '',
    password: '',
    isLoggedIn: false // Добавляем состояние для проверки успешной авторизации
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:8000/api/login/`, userData);
      console.log(response.data);
      // Устанавливаем isLoggedIn в true после успешной авторизации
      setUserData({ ...userData, isLoggedIn: true });
    } catch (error) {
      console.error(error);
      // Обработка ошибок
    }
  };

  // Перенаправление после успешной авторизации
  if (userData.isLoggedIn) {
    return <Redirect to="/products" />;
  }

  return (
    <div>
      <h2>Авторизация</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Логин:
            <input type="text" name="username" value={userData.username} onChange={handleChange} />
          </label>
        </div>
        <div>
          <label>
            Пароль:
            <input type="password" name="password" value={userData.password} onChange={handleChange} />
          </label>
        </div>
        <button type="submit">Войти</button>
      </form>
    </div>
  );
};

export default LoginForm;
