import React from 'react';
import styled from 'styled-components';
import Header from './components/Header';
import SubHeader from './components/SubHeader';
import Rectangle43 from "./Rectangle43.png";
import Footer from './components/Footer';
import { useMediaQuery } from 'react-responsive';

const DeliveryPageContainer = styled.div`
    background-color: #1A1A1A;
    color: #F0F0F0;
    font-family: Montserrat, sans-serif;
`;

const DeliveryPageTitle = styled.h1`
    font-family: Montserrat, sans-serif;
    font-weight: 400;
    font-size: 3.3vw;
    margin-bottom: 7%;
    text-align: center;
    color: #FFFFFF;
`;

const DeliveryPageContent = styled.div`
    max-width: 70%;
    margin: 0 auto;
    padding: 2%;
`;

const ImageContainer = styled.div`
    max-width: 100%;

    display: flex;
    justify-content: center;
    align-items: center;
    margin-bottom: 2%;
`;

const Image = styled.img`
    width: 90%;
    height: 70%;
    border-radius: 55px;
    box-shadow: 0px 4px 4px rgba(0, 0, 0, 0.25);
    position: relative;
    display:flex;
`;

const Text = styled.p`
    width: 100%;
    font-family: Montserrat, sans-serif;
    font-weight: 300;
    font-size: 2.5vw;
    color: #F0F0F0;
    text-align: left;
    margin-left: 3%;
`;
const AdvantagesTitle = styled.h2`
    font-family: Montserrat, sans-serif;
    font-weight: 500; /* Medium */
    font-size: 2.5vw;
    color: #F0F0F0;
    margin-top: 10%;
    text-align: left;
`;
const AdvantageList = styled.ul`
    font-family: Montserrat, sans-serif;
    font-weight: lighter;
    font-size: 2.1vw;
    color: #F0F0F0;
    list-style-type: disc;
    margin-left: 3%;
`;

const AdvantageListItem = styled.li`
    margin-bottom: 2%;
`;
const PaymentTitle = styled.h2`
    font-family: Montserrat, sans-serif;
    font-weight: 500; /* Меняем стиль на Medium */
    font-size: 2.5vw;
    color: #F0F0F0;
    margin-top: 7%; /* Добавляем отступ сверху */
    margin-bottom: 2%; /* Добавляем отступ снизу */
    text-align: right; /* Выравниваем текст по левому краю */
`;

const PaymentMethods = styled.ul`
    font-family: Montserrat, sans-serif;
    font-weight: lighter; /* Делаем текст тоньше */
    font-size: 2.1vw;
    color: #F0F0F0;
    margin-left: 2%;
    text-align: right;
    margin-bottom: 7%;
`;

