// В файле ProductDetail.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import Header from './components/Header';
import SubHeader from './components/SubHeader';

const Container = styled.div`
    max-width: 800px;
    margin: 0 auto;
    padding: 20px;
    display: flex;
`;

const ProductInfo = styled.div`
    flex: 1;
    margin-right: 20px;
`;

const ProductImage = styled.img`
    max-width: 100%;
    height: auto;
    margin-bottom: 20px;
`;

const ProductName = styled.h1`
    font-family: Montserrat, sans-serif;
    font-size: 64px;
    margin-bottom: 20px;
`;

const Characteristics = styled.ul`
    font-size: 18px;
    list-style-type: disc;
    padding-left: 20px;
`;

const ProductDetail = () => {
    const { id } = useParams();
    const [product, setProduct] = useState(null);

    useEffect(() => {
        axios.get(`http://localhost:8000/api/products/${id}`)
            .then(response => {
                setProduct(response.data);
            })
            .catch(error => {
                console.error('Error fetching product:', error);
            });
    }, [id]);

    if (!product) {
        return <div>Loading...</div>;
    }

    return (
        <Container>
            <Header />
            <SubHeader />
            <ProductInfo>
                <ProductName>{product.name}</ProductName>
                <ProductImage src={product.image} alt={product.name} />
            </ProductInfo>
            <Characteristics>
                <li>Characteristics 1: {product.characteristics1}</li>
                <li>Characteristics 2: {product.characteristics2}</li>
                <li>Characteristics 3: {product.characteristics3}</li>
                {/* Добавьте другие характеристики по необходимости */}
            </Characteristics>
        </Container>
    );
}

export default ProductDetail;
