// AuthProvider.js
import React, { createContext, useContext, useEffect, useState } from 'react';

const AuthContext = createContext();

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = getCookie('token');
    if (token) {
      // В случае, если токен найден в cookie, устанавливаем пользователя
      setUser({ token }); // Здесь можно также отправить запрос на сервер для получения данных пользователя по токену
    }
  }, []);

  const login = (token) => {
    // Сохраняем токен в cookie
    setCookie('token', token, { expires: 1 }); // Токен будет храниться в cookie на 1 день
    setUser({ token });
  };

  const logout = () => {
    // Удаляем токен из cookie
    deleteCookie('token');
    setUser(null);
  };

  // Функции для работы с cookie

  const setCookie = (name, value, options = {}) => {
    options = {
      path: '/',
      ...options
    };

    if (options.expires instanceof Date) {
      options.expires = options.expires.toUTCString();
    }

    let updatedCookie = encodeURIComponent(name) + '=' + encodeURIComponent(value);

    for (let optionKey in options) {
      updatedCookie += '; ' + optionKey;
      let optionValue = options[optionKey];
      if (optionValue !== true) {
        updatedCookie += '=' + optionValue;
      }
    }

    document.cookie = updatedCookie;
  };

  const getCookie = (name) => {
    let matches = document.cookie.match(new RegExp(
      '(?:^|; )' + name.replace(/([\.$?*|{}\(\)\[\]\\\/\+^])/g, '\\$1') + '=([^;]*)'
    ));
    return matches ? decodeURIComponent(matches[1]) : undefined;
  };

  const deleteCookie = (name) => {
    setCookie(name, '', { expires: new Date(0) });
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};
