import React, { useEffect, useState } from 'react';
import ProductCardList from './components/ProductCard';
import axios from 'axios';
import styled from 'styled-components';
import Header from './components/Header'
import SubHeader from './components/SubHeader'


const ProductContent = styled.div`
    width: 70%; /* Задаем ширину 80% */
    margin: 0 auto; /* Центрируем по горизонтали */
`;

const CategoryContainer = styled.div`
    display: flex;
    align-items: center;
    margin-bottom: 20px;
`;

const CategoryTitle = styled.div`
    width: 480px;
    height: 750px;
    background-color: #FFCD6E;
    box-shadow: 0 0 65.1px #FFCD6E;
    border-radius: 31px;
    display: flex;
    background-image: ${props => `url(${props.backgroundImage})`}; /* Добавляем фоновое изображение */
    justify-content: center;
    align-items: center;
`;

const ProductLinksContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    padding-left: 20px;
`;

function ProductList() {
    const [categories, setCategories] = useState([]);

    useEffect(() => {
        axios.get(`http://localhost:8000/api/categories/`)
            .then(response => {
                setCategories(response.data);
            })
            .catch(error => {
                console.error('Error fetching categories:', error);
            });
    }, []);

    return (
    <div>
            <Header />
            <SubHeader />
        <ProductContent className="product_content"> {/* Применяем стили для .product_content */}
            {categories.map(category => (
                <CategoryContainer key={category.id}>
                    <CategoryTitle>{category.name}</CategoryTitle>
                    <ProductLinksContainer>
                        <ProductCardList categoryId={category.id} />
                    </ProductLinksContainer>
                </CategoryContainer>
            ))}
        </ProductContent>
        </div>
    );
}

export default ProductList;
