import React from 'react';
import Header from './components/Header';
import SubHeader from './components/SubHeader';
import styled from 'styled-components';
import Footer from './components/Footer';
import MapComponent from './components/Map';
import { useMediaQuery } from 'react-responsive';



const PageContainer = styled.div`
    background-color: #1A1A1A;

`;

const ContentContainer = styled.div`
    max-width: 70%;
    margin: 0 auto;
    padding: 2%;
    display: flex;
    flex-wrap: nowrap;
    align-items: flex-start; /* Изменено на flex-start */
    background-color: #1A1A1A;
`;
const ContentItem = styled.div`
    flex: 1; /* Элемент будет гибко реагировать на изменение размера */
`;

const Rectangle = styled.div`
    position: relative;
    width: 100%;
    background-color: #353333;
    border-radius: 40px;
    box-shadow: 0px 11px 28.7px rgba(0, 0, 0, 0.25);
    margin-top: 7%;
    padding: 3%;
`;

const SmallRectangleLeft = styled.div`
    width: 20em;
    height: 25em;
    position: relative;
    background-color: #353333;
    border-radius: 40px;
    box-shadow: 0px 10px 20.3px rgba(0, 0, 0, 0.25);
    margin-top: 1%;
    left: 0%;
    padding: 2%;
    margin-right: 2.5%;
`;




const Heading = styled.h1`
    font-family: Montserrat, sans-serif;
    font-weight: 400;
    font-size: 4vw;
    color: #F0F0F0;
    margin-bottom: 20px;
    text-align: center; /* Добавлено выравнивание текста по центру */
    width: 100%; /* Добавлено, чтобы текст занимал всю ширину контейнера */
`;

const ContactList = styled.ul`
    font-family: Montserrat, sans-serif;
    font-weight: 300;
    font-size: 1.6vw;
    color: #F0F0F0;
    list-style-type: disc;
`;

const ContactListItem = styled.li`
    margin-bottom: 5%;
`;

const AddressTitle = styled.h2`
    font-family: Montserrat, sans-serif;
    font-weight: 500;
    font-size: 2.5vw;
    color: #F0F0F0;
    margin-bottom: 2%;
`;

const AddressText = styled.p`
    font-family: Montserrat, sans-serif;
    font-weight: 300;
    font-size: 1.6vw;
    color: #F0F0F0;
`;
const InfoRectangle = styled.div`
    width: 20rem;
    height: 24.7rem;
    background-color: #353333;
    border-radius: 40px;
    box-shadow: 0px 10px 20.3px rgba(0, 0, 0, 0.25);
    margin-top: 1%;
    margin-left: 2.5%;
    padding: 2.2%;
    color: #F0F0F0;
`;

const InfoTitle = styled.h2`
    font-family: Montserrat, sans-serif;
    font-weight: 500;
    font-size: 2.5vw;
`;

const InfoText = styled.p`
    font-family: Montserrat, sans-serif;
    font-weight: 300; /* Стиль Lighter */
    font-size: 1.7vw;
`;

const AboutRectangle = styled.div`
    width: 100%; /* Ширина 70% страницы */
    margin: 0 auto; /* Центрирование по горизонтали */
    margin: 1% auto 0;
    background-color: #353333;
    border-radius: 40px;
    box-shadow: 0px 11px 28.7px rgba(0, 0, 0, 0.25); /* Тень со сдвигом по y на 11px и blur 28.7 */
    padding: 30px;
    color: #F0F0F0;
