// ProductCard.js
import React from 'react';
import './ProductCard.css';

const ProductCard = () => {
  // Функция для получения рандомной ссылки на изображение
  const getRandomImageUrl = () => {
    const randomNumber = Math.floor(Math.random() * 1000); // Генерируем случайное число от 0 до 999
    return `https://source.unsplash.com/random/480x750?${randomNumber}`;
  };

  return (
    <div className="product-card">
      {/* Левая часть: карточка товара */}
      <div className="product-info" style={{ backgroundImage: `url(${getRandomImageUrl()})` }}>
        <div className="text-overlay">
          <h3>Эпоксидные смолы</h3> универсальные материалы, применяемые в строительстве, ремонте, производстве изделий из стекловолокна и творчестве. Они обладают высокой прочностью, стойкостью к химическим воздействиям и воде, идеально подходят для заливки, клеения и покрытий.
        </div>
      </div>
      {/* Правая часть: четыре изображения */}
      <div className="product-images">
        <div className="image-row">
          <img src={getRandomImageUrl()} alt="Image 1" />
          <img src={getRandomImageUrl()} alt="Image 2" />
        </div>
        <div className="image-row">
          <img src={getRandomImageUrl()} alt="Image 3" />
          <img src={getRandomImageUrl()} alt="Image 4" />
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
