import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../../AuthProvider'; // Импортируем хук useAuth

const LoginForm = () => {
  const { setUser } = useAuth(); // Получаем setUser из контекста аутентификации
  const [userData, setUserData] = useState({
    username: '',
    password: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:8000/api/login/`, userData);
      const { token, user } = response.data; // Получаем токен и информацию о пользователе из ответа сервера
      console.log('User token:', token); // Выводим токен пользователя в консоль
      localStorage.setItem('token', token); // Сохраняем токен в локальном хранилище
      setUser(user); // Сохраняем информацию о пользователе в контексте аутентификации
      console.log('Authentication successful');

    } catch (error) {
      console.error('Authentication failed:', error);
    }
  };

  return (
    <div>
      <h2>Авторизация</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Логин:
            <input type="text" name="username" value={userData.username} onChange={handleChange} />
          </label>
        </div>
        <div>
          <label>
            Пароль:
            <input type="password" name="password" value={userData.password} onChange={handleChange} />
          </label>
        </div>
        <button type="submit">Войти</button>
      </form>
    </div>
  );
};

export default LoginForm;
